DROP DATABASE IF EXISTS advertising_site;
CREATE DATABASE advertising_site;
USE advertising_site;

DROP TABLE IF EXISTS users;
CREATE TABLE users ( 
            id SERIAL PRIMARY KEY,
            firstname VARCHAR(50) COMMENT 'Имя',
            lastname VARCHAR(50) COMMENT 'Фамилия',
            phone BIGINT UNIQUE,
            email VARCHAR(100) UNIQUE,
            is_deleted BIT default b'0',
            INDEX users(firstname, lastname)
            );
           
DROP TABLE IF EXISTS media;
CREATE TABLE media (		
			id SERIAL PRIMARY KEY,
			user_id BIGINT UNSIGNED NOT NULL,
			filename VARCHAR(255),
			size_file BIGINT,
			created_at DATETIME DEFAULT NOW(),
    		updated_at DATETIME DEFAULT CURRENT_TIMESTAMP ON UPDATE CURRENT_TIMESTAMP,
			link VARCHAR(255) NOT NULL,
		    FOREIGN KEY (user_id) REFERENCES users(id) ON UPDATE CASCADE ON DELETE CASCADE
			);    
            
DROP TABLE IF EXISTS profiles;
CREATE TABLE profiles (            
			user_id SERIAL PRIMARY KEY,
			gender CHAR(1),
			birthday DATE,
			photo_id BIGINT UNSIGNED,
    		created_at DATETIME DEFAULT NOW(),
    		hometown VARCHAR(100),
            FOREIGN KEY (user_id) REFERENCES users(id) ON UPDATE CASCADE ON DELETE CASCADE,
            FOREIGN KEY (photo_id) REFERENCES media(id) ON UPDATE CASCADE ON DELETE CASCADE
            );
            
DROP TABLE IF EXISTS messages;
CREATE TABLE messages (
			id SERIAL PRIMARY KEY,
            from_user BIGINT UNSIGNED NOT NULL,
            to_user BIGINT UNSIGNED NOT NULL,
            body TEXT,
            media_id BIGINT UNSIGNED NOT NULL,
            created_at DATETIME DEFAULT NOW(),
            FOREIGN KEY (from_user) REFERENCES users(id) ON UPDATE CASCADE ON DELETE CASCADE,
    		FOREIGN KEY (to_user) REFERENCES users(id) ON UPDATE CASCADE ON DELETE CASCADE,
    		FOREIGN KEY (media_id) REFERENCES media(id) ON UPDATE CASCADE ON DELETE CASCADE
			);
		
		
DROP TABLE IF EXISTS ads_category;
CREATE TABLE ads_category (
			id SERIAL PRIMARY KEY,
			name VARCHAR(50),
			created_at DATETIME DEFAULT NOW()
			);
            
DROP TABLE IF EXISTS ads;
CREATE TABLE ads (
			id SERIAL PRIMARY KEY,
			user_id BIGINT UNSIGNED NOT NULL,
			category_id BIGINT UNSIGNED NOT NULL,
			name VARCHAR(255),
			body TEXT,
			price INT UNSIGNED NOT NULL,
			address TEXT,
			media_id BIGINT UNSIGNED NOT NULL,
			status ENUM("Активно", "Архив", "Не активно"),
			INDEX ads(name),
			FOREIGN KEY (user_id) REFERENCES users(id) ON UPDATE CASCADE ON DELETE CASCADE,
			FOREIGN KEY (category_id) REFERENCES ads_category(id) ON UPDATE CASCADE ON DELETE CASCADE,
			FOREIGN KEY (media_id) REFERENCES media(id) ON UPDATE CASCADE ON DELETE CASCADE
			);
			
DROP TABLE IF EXISTS rewiews;
CREATE TABLE rewiews (
			from_user_id BIGINT UNSIGNED NOT NULL,
			to_ads_id BIGINT UNSIGNED NOT NULL,
			body TEXT,
			grade ENUM("1", "2", "3", "4", "5"),
			created_at DATETIME DEFAULT NOW(),
			PRIMARY KEY (from_user_id, to_ads_id),
			FOREIGN KEY (from_user_id) REFERENCES users(id) ON UPDATE CASCADE ON DELETE CASCADE,
			FOREIGN KEY (to_ads_id) REFERENCES ads(id) ON UPDATE CASCADE ON DELETE CASCADE
			);        
            
DROP TABLE IF EXISTS favorites;
CREATE TABLE favorites (
			from_user_id BIGINT UNSIGNED NOT NULL,
			to_ads_id BIGINT UNSIGNED NOT NULL,
			created_at DATETIME DEFAULT NOW(),
			PRIMARY KEY (from_user_id, to_ads_id),
			FOREIGN KEY (from_user_id) REFERENCES users(id) ON UPDATE CASCADE ON DELETE CASCADE,
			FOREIGN KEY (to_ads_id) REFERENCES ads(id) ON UPDATE CASCADE ON DELETE CASCADE
			);   
            
DROP TABLE IF EXISTS orders;
CREATE TABLE orders (
			id SERIAL PRIMARY KEY,
			user_id BIGINT UNSIGNED NOT NULL,
			ads_id BIGINT UNSIGNED NOT NULL,
			order_date DATETIME DEFAULT NOW(),
			status ENUM("Оплачен", "Ожидает отправки", "Отправлен", "Поступил в точку выдачи", "Получен", "Отменен"),
			FOREIGN KEY (user_id) REFERENCES users(id) ON UPDATE CASCADE ON DELETE CASCADE,
			FOREIGN KEY (ads_id) REFERENCES ads(id) ON UPDATE CASCADE ON DELETE CASCADE
			);   
            
DROP TABLE IF EXISTS wallet;
CREATE TABLE wallet (
			user_id SERIAL PRIMARY KEY,
			currency VARCHAR(10),
			balance BIGINT,
			FOREIGN KEY (user_id) REFERENCES users(id) ON UPDATE CASCADE ON DELETE CASCADE
			);

